html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,font,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td{margin:0;padding:0;border:0;outline:0;font-size:100%;vertical-align:baseline;background:transparent;}body{line-height:1;}ol,ul{list-style:none;}blockquote,q{quotes:none;}blockquote:before,blockquote:after,q:before,q:after{content:'';content:none;}:focus{outline:0;}ins{text-decoration:none;}del{text-decoration:line-through;}table{border-collapse:collapse;border-spacing:0;}html{background-color:#fff;background-image:url("http://a1.twimg.com/profile_background_images/170549220/bg.jpg2");background-attachment:fixed;}html,body{margin:0;padding:0;}body{font-family:Tahoma,Verdana,"Trebuchet MS",verdana;font-size:.8em;color:#666666;}a,a:visited,a:active{color:#3366CC;text-decoration:none;}a:hover{text-decoration:underline;}h1{font-size:1.5em;padding:5px 0;}h2{font-size:1.1em;padding:5px 0;}div#main{position:relative;margin:20px auto;width:750px;background-color:transparent;}div#cab{background-position:0 47px;overflow:hidden;}div.social{width:100%;height:47px;}div.social > ul{float:right;margin:10px;}div.social > ul > li{float:left;padding:5px 10px;font-size:1em;}div.social > ul > li > a{color:white;}div.logo{text-align:center;}img.logo{}div.menu{float:right;overflow:hidden;}div.menu > ul > li.menu_esq{padding:0;width:20px;height:40px;background-image:url("http://centraldemangas.com.br/img/menu_esq.png");background-repeat:no-repeat;background-position:left top;}div.menu > ul > li.menu_dir{padding:0;width:20px;height:40px;background-image:url("http://centraldemangas.com.br/img/menu_dir.png");background-repeat:no-repeat;background-position:right top;}div.menu > ul > li.menu_sep{padding:0;width:4px;height:40px;background-image:url("http://centraldemangas.com.br/img/menu_div.png");}div.menu > ul > li{float:left;background-image:url("http://centraldemangas.com.br/img/menu.png");background-repeat:repeat-x;text-decoration:none;}div.menu > ul > li > a{display:block;float:left;margin:0;padding:10px 24px 0 24px;height:30px;color:#fff;}div.menu > ul > li > a{text-decoration:none;}div.menu > ul > li.menu_item:hover{background-position:0 -40px;text-shadow:#fff 0 0 5px;}div.top_azul{background-color:#1FAEE8;height:30px;-moz-border-radius:7px;-webkit-border-radius:7px;border-radius:7px;z-index:-1;}div#principal{position:relative;margin-top:-15px;background-color:#fff;overflow:hidden;-moz-border-radius:7px;-webkit-border-radius:7px;border-radius:7px;z-index:2;}div#barra{float:left;margin:0 0;width:180px;}div#conteudo{float:left;margin:10px 0;padding:10px;width:550px;background-color:white;}div#anuncio{float:left;margin:10px;width:160px;}div#foot{position:relative;margin-top:-30px;padding-top:33px;padding-bottom:3px;background-color:#fff;background-color:#1FAEE8;color:white;text-align:center;font-size:.7em;-moz-border-radius:7px;-webkit-border-radius:7px;border-radius:7px;z-index:1;}div#foot a{color:white;}div#fim{height:200px;}div#slide{display:none;overflow:hidden;}div.bloco{margin:10px;}div.b2{float:left;width:50%;overflow:hidden;}div.destaque a{font-size:2em;}div.destaque img{margin:10px;width:85%;}div.update div.dados{margin:15px 0;}div.update div.dados span.data{font-family:tahoma;font-size:.85em;}div.pag{position:relative;display:block;}div.menos{float:left;}div.mais{float:right;}.tbl_barra{margin-bottom:25px;width:100%;font-size:.85em;border-collapse:separate;}.tbl_barra caption,.tbl_barra thead{padding:0 10px;color:black;font-family:Arial Black;font-size:1.1em;text-align:left;}.tbl_barra td{padding:8px 10px;border-top:1px solid #efefef;border-bottom:1px solid #ddd;}.tbl_barra tr:first-child > td{border-top:none;}.tbl_barra tr:last-child > td{border-bottom:none;}.tbl_mangas{width:100%;border-collapse:collapse;font-size:1em;}.tbl_mangas thead{font-weight:bold;text-align:center;}.tbl_mangas th,.tbl_mangas td{padding:5px 10px;border-bottom:1px solid #dddddd;}span.link_manga{font-size:1.5em;}span.caps{font-size:.8em;color:#666666;}div.link_down{margin:2px;padding:2px 5px;border:1px solid #f1f1f1;-moz-border-radius:5px;-webkit-border-radius:5px;border-radius:5px;font-size:1.5em;}div.link_down:hover{background-color:#f5f5f5;}div.link_excluir{float:right;width:20px;-moz-border-radius:5px;-webkit-border-radius:5px;border-radius:5px;cursor:pointer;background-color:darkred;color:white;text-align:center;font-weight:bold;}div.link_excluir a{color:white;}ul.importante{margin:20px;padding:0;list-style:disc;text-align:justify;}ul.importante > li{padding:5px 0;}div.login{position:relative;margin:20px auto;padding:20px;width:300px;background-color:white;text-align:center;}div.dados{padding:10px;}div.adm_menu > ul{padding:0 25px;list-style-type:square;}div.adm_menu > ul > li{padding:2px 0;}table.form,table.lista{margin:10px;}table.form td,table.lista td{vertical-align:middle;}table.lista thead td{font-weight:bold;text-align:center;}table.lista td{padding:2px;border:1px solid #3366CC;}table.lista tbody{font-size:.8em;}div.rotulos{padding-bottom:3px;overflow:hidden;}span.rotulo{float:left;margin:2px;padding:2px 5px;-webkit-border-radius:5px;-moz-border-radius:5px;border-radius:5px;-webkit-box-shadow:3px 3px 3px #888;-moz-box-shadow:3px 3px 3px #888;box-shadow:3px 3px 3px #888;background-color:#fafafa;font-size:.8em;cursor:pointer;}span.tem{background-color:#0a0;color:white;}span.nao_tem{background-color:#f55;color:white;}#botao_cdm{width:100%;height:30px;font-family:courier new;font-size:.8em;}.coin-slider{overflow:hidden;zoom:1;position:relative;}.coin-slider a{text-decoration:none;outline:none;border:none;}.cs-buttons{font-size:0px;padding:10px;float:left;}.cs-buttons a{margin-left:5px;height:10px;width:10px;float:left;border:1px solid #B8C4CF;color:#B8C4CF;text-indent:-1000px;}.cs-active{background-color:#B8C4CF;color:#FFFFFF;}.cs-title{width:545px;padding:10px;background-color:#000000;color:#FFFFFF;}.cs-prev,.cs-next{background-color:#000000;color:#FFFFFF;padding:0px 10px;}
